mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, N.* FROM ( SELECT i.interior_customer, m.member_name AS customer_name, i.interior_living, i.interior_kitchen, i.interior_bed, i.interior_oneroom, i.interior_kidroom, i.interior_study, i.interior_why, i.interior_payment_date, i.interior_no, NVL(d.interior_status, 0) AS interior_status FROM interior_tbl i LEFT JOIN designer_board_tbl d ON (i.interior_no = d.interior_no) JOIN member_tbl m ON (i.interior_customer = m.member_id) WHERE i.interior_designer = #{designerId}
AND m.member_name LIKE '%' || #{keyword} || '%'
ORDER BY i.interior_payment_date DESC ) N ) WHERE RNUM BETWEEN #{pi.start} AND #{pi.end}

mapper.update:
UPDATE designer_board_tbl
SET interior_memo = #{interiorMemo}, interior_status = #{interiorStatus}
WHERE interior_no = #{interiorNo}
